function loadInt(){loadJSON(url,gotData,"jsonp")}function loadCity(){url=baseurl+formCity.value()+type+mode+appid+unit+lang,loadJSON(url,gotData,"jsonp")}function gotData(t){weatherData=t,windSpeed=1.2*t.list[0].wind.speed,city=t.city.name,country=t.city.country;var o=round(t.city.coord.lon),n=round(t.city.coord.lat);formCity.value(city+", "+country),t.list[0].rain?(amountRain=t.list[0].rain["3h"],amountRain=round(200*amountRain)):amountRain=0,t.list[0].snow?(amountSnow=t.list[0].snow["3h"],amountSnow=round(200*amountSnow)):amountSnow=0,temperature=t.list[0].main.temp,tempColor=t.list[0].main.temp,weatherType=t.list[0].weather[0].id}function reloadCity(){loadCity(),clouds=[]}