function loadInt(){loadJSON(url,gotData,"jsonp")}function loadCity(){url=baseurl+formCity.value()+mode+appid+unit+lang,loadJSON(url,gotData,"jsonp")}function gotData(t){weatherData=t,windSpeed=1.2*t.list[0].wind.speed,t.list[0].rain?(amountRain=t.list[0].rain["3h"],amountRain=round(100*amountRain)):amountRain=0,t.list[0].snow?(amountSnow=t.list[0].snow["3h"],amountSnow=round(100*amountSnow)):amountSnow=0,temperature=t.list[0].main.temp,tempColor=t.list[0].main.temp,weatherType=t.list[0].weather[0].id,console.log(weatherData),console.log(temperature)}