function cloudPush(){var i=-(windowWidth/4),t=-50,s=windowWidth/500,n=1+s;clouds.length<n&&clouds.push(new Cloud(random(i,t),random(.45*windowHeight,.625*windowHeight),round(random(10,150))))}function Cloud(i,t,s){this.x=i,this.y=t,this.lifespan=s,this.radius=100,this.width=150,this.height=120,this.windSpeedMotion=windSpeed/2,this.windowRatioSpeed=windowWidth/100,this.windmovementX=this.windSpeedMotion/this.windowRatioSpeed,this.fadeInOpacity=0,this.fadeInX=windowWidth/2,this.fadeOutX=windowWidth/2+windowWidth/6,this.display=function(){push(),imageMode(CENTER),ellipse(this.x,this.y,this.radius,this.radius),tint(255,this.fadeInOpacity),console.log(this.windmovementX),pop()},this.update=function(){this.x=this.x+this.windSpeedMotion,this.x<this.fadeInX&&(this.lifespan++,this.fadeInOpacity++),this.x>this.fadeOutX&&(this.lifespan--,this.fadeInOpacity--)},this.updateWind=function(){this.windmovementX=this.windSpeedMotion/this.windowRatioSpeed},this.intersects=function(i){var t=dist(this.x,this.y,i.x,i.y);return t<this.radius+i.radius?!0:!1},this.lifespancheck=function(){return this.lifespan<=0?!0:!1}}